themeCSS:
.class-io > rect, .class-io > path { fill: #dae8fc; stroke: #6c8ebf; stroke-width: 2px; }
.class-compute > rect, .class-compute > path { fill: #fff2cc; stroke: #d6b656; stroke-width: 2px; }
.class-data > rect, .class-data > path { fill: #f5f5f5; stroke: #999999; stroke-width: 2px; }
.class-tracker > rect, .class-tracker > path { fill: #e1d5e7; stroke: #9673a6; stroke-width: 2px; }
.class-decision > rect, .class-decision > path { fill: #ffe6cc; stroke: #d79b00; stroke-width: 2px; }
.class-result > rect, .class-result > path { fill: #d5e8d4; stroke: #82b366; stroke-width: 2px; }
.class-step > rect, .class-step > path { fill: #fff2cc; stroke: #d6b656; stroke-width: 2px; }

g.cluster > rect { fill: #f5f5f5; stroke: #999999; stroke-width: 2px; }
