themeCSS:
.class_io > rect, .class_io > path { fill: #dae8fc; stroke: #c2c2c4; stroke-width: 2px; }
.class_compute > rect, .class_compute > path { fill: #54000d; stroke: #d6b656; stroke-width: 2px; }
.class_data > rect, .class_data > path { fill: #f5f5f5; stroke: #999999; stroke-width: 2px; }
.class_tracker > rect, .class_tracker > path { fill: #e1d5e7; stroke: #9673a6; stroke-width: 2px; }
.class_decision > rect, .class_decision > path { fill: #ffe6cc; stroke: #d79b00; stroke-width: 2px; }
.class_result > rect, .class_result > path { fill: #d5e8d4; stroke: #82b366; stroke-width: 2px; }
.class_step > rect, .class_step > path { fill: #fff2cc; stroke: #d6b656; stroke-width: 2px; }

g.cluster > rect { fill: #f5f5f5; stroke: #999999; stroke-width: 2px; }
